package org.apache.commons.validator.routines;

import org.apache.commons.validator.routines.CreditCardValidator;
import org.junit.jupiter.api.Test;

/**
 * Test case to validate that the system's payment card validation capability can be
 * initialized and invoked without encountering initialization failures caused by
 * internal component dependencies.
 */
public class CreditCardValidatorInitializationTest {

    /**
     * Validates that the payment card validation service and the generic validation
     * utilities can coexist and operate without initialization conflicts. This test
     * ensures proper system initialization and execution flow without requiring
     * explicit assertions beyond successful method completion.
     */
    @Test
    public void testPaymentCardValidationInitialization() {
        // Establish a payment card validation service instance to ensure system components load correctly.
        CreditCardValidator validator = new CreditCardValidator();

        // Submit a sample payment card number for validation to confirm the validation logic executes
        // without errors stemming from circular initialization issues.
        validator.isValid("4111111111111111");
    }
}

