package org.apache.commons.validator;

import org.apache.commons.validator.routines.CreditCardValidator;
import org.junit.jupiter.api.Test;

/**
 * Tests the fix for VALIDATOR-502.
 */
public class Validator502Test {

    /**
     * Tests that instantiating a CreditCardValidator followed by a call to
     * GenericValidator.isCreditCard() does not cause a NullPointerException.
     */
    @Test
    public void testValidator502() {
        // Instantiate the routines class first
        final org.apache.commons.validator.routines.CreditCardValidator ccv = new CreditCardValidator();
        // Call the static method which creates an instance of the non-routines class
        GenericValidator.isCreditCard("1234567890");
    }
}

