package org.apache.commons.validator;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertDoesNotThrow;

/**
 * This test verifies that there are no circular dependency issues between
 * GenericValidator and CreditCardValidator during static initialization.
 */
public class CircularDependencyTest {

    /**
     * This test addresses a potential defect where the GenericValidator's static
     * reference to CreditCardValidator functionality would fail if invoked before
     * an explicit instantiation of a CreditCardValidator, due to an incomplete
     * static initialization chain.
     */
    @Test
    public void testGenericValidatorCreditCardInitialization() {
        // Instantiating CreditCardValidator is intended to trigger its static
        // initialization sequence. This sequence involves referencing various validation
        // constants and code validators for card types like AMEX, Visa, etc.
        new CreditCardValidator();

        // Following the instantiation, we call the credit card validation method
        // on GenericValidator. This is to confirm that the method executes without
        // a NullPointerException, which would indicate a circular dependency issue.
        // The assertion confirms that the call completes, showing that GenericValidator
        // can access CreditCardValidator's static members without initialization conflicts.
        assertDoesNotThrow(() -> {
            GenericValidator.isCreditCard("4111111111111111");
        }, "GenericValidator.isCreditCard should execute successfully after CreditCardValidator instantiation.");
    }
}
