package com.example.app;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;

import java.sql.DriverManager;
import java.sql.Driver;
import java.util.Enumeration;

public class DriverRegistrationTest {

    @Test
    void testH2DriverRegistration() {
        // Step 1: Obtain H2 driver identification
        String h2DriverIdentification = "org.h2.Driver";

        // Step 2: Register the H2 driver using its identification
        boolean registrationResult = org.apache.commons.dbutils.DbUtils.loadDriver(h2DriverIdentification);

        // Step 3: Assert that the registration result indicates success
        assertTrue(registrationResult, "H2 driver registration should return true");

        // Step 4 & 5: Verify the H2 driver is available for establishing connections
        // and confirm its presence in the system's driver registry.
        boolean driverFound = false;
        Enumeration<Driver> drivers = DriverManager.getDrivers();
        while (drivers.hasMoreElements()) {
            Driver driver = drivers.nextElement();
            if (driver.getClass().getName().equals(h2DriverIdentification)) {
                driverFound = true;
                break;
            }
        }
        assertTrue(driverFound, "H2 driver should be found in DriverManager's registered drivers");
    }
}

