/**
 * Tests for {@link ExecuteWatchdog.Builder}.
 */
public class ExecuteWatchdogTest1 {

    @Test
    public void testExecuteWatchdogBuilder() {
        // Step 1 & 2: Basic builder validation
        final ExecuteWatchdog.Builder watchdogBuilder = ExecuteWatchdog.builder();
        final ExecuteWatchdog watchdogInstance = watchdogBuilder.get();
        assertNotNull(watchdogInstance);

        // Step 3-6: Default (no timeout) behavior
        final ExecuteWatchdog.Builder watchdogBuilder2 = ExecuteWatchdog.builder();
        final ExecuteWatchdog watchdogInstance2 = watchdogBuilder2.get();
        final Watchdog internalWatchdog = watchdogInstance2.getWatchdog();
        assertNull(internalWatchdog);

        // Step 7-11: Null timeout behavior
        final Duration nullTimeout = null;
        final ExecuteWatchdog.Builder watchdogBuilder3 = ExecuteWatchdog.builder();
        watchdogBuilder3.setTimeout(nullTimeout);
        final ExecuteWatchdog watchdogInstance3 = watchdogBuilder3.get();
        assertNotNull(watchdogInstance3);

        // Step 12-18: Timeout propagation
        final Duration oneMinuteDuration = Duration.ofMinutes(1);
        final ExecuteWatchdog.Builder watchdogBuilder4 = ExecuteWatchdog.builder();
        watchdogBuilder4.setTimeout(oneMinuteDuration);
        final ExecuteWatchdog watchdogInstance4 = watchdogBuilder4.get();
        final Watchdog internalWatchdog4 = watchdogInstance4.getWatchdog();
        final Duration configuredTimeout = internalWatchdog4.getTimeout();
        assertEquals(oneMinuteDuration, configuredTimeout);

        // Step 19-23: Null thread factory behavior
        final ThreadFactory nullThreadFactory = null;
        final ExecuteWatchdog.Builder watchdogBuilder5 = ExecuteWatchdog.builder();
        watchdogBuilder5.setThreadFactory(nullThreadFactory);
        final ExecuteWatchdog watchdogInstance5 = watchdogBuilder5.get();
        assertNotNull(watchdogInstance5);

        // Step 24-32: Chained configuration
        final ThreadFactory nullThreadFactory2 = null;
        final Duration oneMinuteDuration2 = Duration.ofMinutes(1);
        final ExecuteWatchdog.Builder watchdogBuilder6 = ExecuteWatchdog.builder();
        watchdogBuilder6.setThreadFactory(nullThreadFactory2);
        watchdogBuilder6.setTimeout(oneMinuteDuration2);
        final ExecuteWatchdog watchdogInstance6 = watchdogBuilder6.get();
        final Watchdog internalWatchdog6 = watchdogInstance6.getWatchdog();
        final ThreadFactory retrievedThreadFactory = internalWatchdog6.getThreadFactory();
        assertNotNull(retrievedThreadFactory);
    }
}

