package org.apache.commons.exec.util;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertThrows;

public class StringUtilsQuoteArgumentTest {

    @Test
    public void testQuoteArgument() {
        // Variables based on supervisor instructions and scenario steps
        String content_with_space = "unquoted content";
        String single_quoted_string = "'" + content_with_space + "'";
        String double_quoted_string = "\"" + content_with_space + "\"";
        String string_with_embedded_single_quotes = "it's a test";
        String expected_double_quoted_output = "\"it's a test\"";
        String string_with_embedded_double_quotes = "a \"test\"";
        String expected_single_quoted_output = "'a \"test\"'";
        String string_with_mixed_quotes = "it's a \"test\"";

        // Step 0 & 1: Call with single-quoted string and assert
        // The method strips quotes and then re-quotes if spaces are present.
        String result1 = StringUtils.quoteArgument(single_quoted_string);
        assertEquals("\"" + content_with_space + "\"", result1);

        // Step 2 & 3: Call with double-quoted string and assert
        String result2 = StringUtils.quoteArgument(double_quoted_string);
        assertEquals("\"" + content_with_space + "\"", result2);

        // Step 4 & 5: Call with embedded single quotes and assert
        String result3 = StringUtils.quoteArgument(string_with_embedded_single_quotes);
        assertEquals(expected_double_quoted_output, result3);

        // Step 6 & 7: Call with embedded double quotes and assert
        String result4 = StringUtils.quoteArgument(string_with_embedded_double_quotes);
        assertEquals(expected_single_quoted_output, result4);

        // Step 8 & 9: Call with mixed quotes and assert exception
        assertThrows(IllegalArgumentException.class, () -> {
            StringUtils.quoteArgument(string_with_mixed_quotes);
        });
    }
}

