package org.apache.commons.validator;

import org.junit.jupiter.api.Test;

/**
 * This test verifies that instantiating a CreditCardValidator does not cause a NullPointerException
 * when GenericValidator.isCreditCard is called. This addresses the circular dependency
 * issue reported in VALIDATOR-502.
 */
public class GenericValidatorTest1 {

    @Test
    public void testValidator502() {
        // Instantiate CreditCardValidator to trigger static initialization
        org.apache.commons.validator.routines.CreditCardValidator ccv = new org.apache.commons.validator.routines.CreditCardValidator();

        // Call the method that was causing a NullPointerException
        // This test passes if no exception is thrown.
        org.apache.commons.validator.GenericValidator.isCreditCard("1234567890");
    }
}

