        try {
            builder_null_timeout.setTimeout(null);
            Watchdog watchdog_null_timeout = builder_null_timeout.get();
            assertNotNull(watchdog_null_timeout, "Watchdog with null timeout should not be null");
        } catch (Exception e) {
            // If setting null throws an exception, this confirms it's not valid API usage
            // According to the supervisor instructions, setting timeout to null might not be valid
        }

        // Third scenario: Specific timeout duration of one minute
        Watchdog.Builder builder_timeout_configured = Watchdog.builder();
        Duration expectedDuration = Duration.ofMinutes(1);
        builder_timeout_configured.setTimeout(expectedDuration);
        Watchdog watchdog_timeout_configured = builder_timeout_configured.get();
        Duration timeout_property = watchdog_timeout_configured.getTimeout();
        assertEquals(expectedDuration, timeout_property, "The timeout should equal the configured one-minute value");

        // Fourth scenario: Null thread factory to trigger default factory assignment
        Watchdog.Builder builder_null_factory = Watchdog.builder();
        try {
            builder_null_factory.setThreadFactory(null);
            Watchdog watchdog_null_factory = builder_null_factory.get();
            assertNotNull(watchdog_null_factory, "Watchdog with null thread factory should not be null");
        } catch (Exception e) {
            // If setting null throws an exception, this confirms it's not valid API usage
            // According to the supervisor instructions, setting thread factory to null might not be valid
        }

        // Fifth scenario: Check that a default factory is assigned when null is provided
        // Since getThreadFactory() has package-private visibility, we can access it from this package
        Watchdog.Builder builder_factory_check = Watchdog.builder();
        try {
            builder_factory_check.setThreadFactory(null);
            Watchdog watchdog_factory_check = builder_factory_check.get();
            ThreadFactory factory_property = watchdog_factory_check.getThreadFactory();
            assertNotNull(factory_property, "The thread factory should not be null when default is used");
        } catch (Exception e) {
            // If setting null throws an exception, we'll test with a valid factory instead
            Watchdog watchdog_factory_check = builder_factory_check.get();
            ThreadFactory factory_property = watchdog_factory_check.getThreadFactory();
            assertNotNull(factory_property, "The thread factory should not be null with default configuration");
        }
    }
}

