package org.apache.commons.validator;

import org.apache.commons.validator.routines.CreditCardValidator;
import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;

/**
 * Test to verify that there are no circular dependency issues during static initialization
 * between the Generic Validator and Credit Card Validator components.
 */
public class CreditCardValidatorInitializationTest {

    @Test
    public void testStaticInitializationCircularDependency() {
        // Instantiate Credit Card Validator to trigger its static initialization sequence
        CreditCardValidator creditCardValidator = new CreditCardValidator();

        // Invoke credit card validation method on Generic Validator with numeric string input
        // This checks if GenericValidator can access CreditCardValidator's static members
        // without encountering a null pointer exception due to circular dependency
        String testCardNumber = "4444111122223333";

        // Assert that validation call completes successfully without null pointer exception
        assertDoesNotThrow(() -> {
            GenericValidator.isCreditCard(testCardNumber);
        }, "Validation completed without throwing NullPointerException");

        // Confirm that the validation returns a valid result (true or false)
        // We're just confirming it doesn't throw an exception and returns a boolean
        boolean validationResult = GenericValidator.isCreditCard(testCardNumber);
        assertTrue(validationResult || !validationResult, "Validation returned a boolean result");
    }
}
